using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string markdown = "This would be *italicized* text and this would be **bold** text";\n        \n        // Convert bold\n        string html = Regex.Replace(markdown, @"\*\*(.+?)\*\*", "<strong>$1</strong>");\n        // Convert italic\n        html = Regex.Replace(html, @"\*(.+?)\*", "<em>$1</em>");\n\n        Console.WriteLine(html);\n    }\n}