using System.Reflection;\nusing System.Windows.Forms;\n\npublic void SetCustomCursor()\n{\n    // Assuming you have added your custom_cursor.cur as an embedded resource under Resources\n    Assembly assembly = Assembly.GetExecutingAssembly();\n    var stream = assembly.GetManifestResourceStream("YourNamespace.Resources.custom_cursor.cur");\n    \n    using (var ms = new MemoryStream())\n    {\n        byte[] data = new byte[stream.Length];\n        stream.Read(data, 0, data.Length);\n        ms.SetLength(data.Length);\n        ms.Write(data, 0, data.Length);\n\n        // Load the custom cursor\n        Mouse.OverrideCursor = new Cursor(ms);\n    }\n}