using System;\nusing System.Text;\n\npublic class Base64UrlEncoderDecoder\n{\n    public static string EncodeBase64Url(string input)\n    {\n        byte[] bytes = Encoding.UTF8.GetBytes(input);\n        string base64 = Convert.ToBase64String(bytes);\n\n        // Modify for URL safe encoding\n        string modifiedBase64Url = base64.Replace('+', '-').Replace('/', '_');\n\n        // Remove padding characters\n        modifiedBase64Url = modifiedBase64Url.TrimEnd('=');\n        return modifiedBase64Url;\n    }\n\n    public static string DecodeBase64Url(string input)\n    {\n        // Add back padding removed during encoding\n        int padCount = 3 - (input.Length % 4);\n        if (padCount != 0 && padCount != 3) {\n            input += new string('=', padCount);\n        }\n\n        // Replace URL safe characters\n        input = input.Replace('-', '+').Replace('_', '/');\n\n        byte[] bytes = Convert.FromBase64String(input);\n\n        return Encoding.UTF8.GetString(bytes);\n    }\n}