protected void TestSubmit_ServerClick(object sender, EventArgs e)\n{\n    // Initialize the connection string.\n    string connectionString = @"Data Source=localhost\SQLEXPRESS;Initial Catalog=Inputs;Integrated Security=SSPI";\n\n    using (SqlConnection connectSQL = new SqlConnection(connectionString))\n    {\n        try\n        {\n            // Setup and execute the command.\n            SqlCommand theCommand = new SqlCommand("SELECT ApplicationName FROM Inputs", connectSQL);\n            connectSQL.Open();\n\n            using (SqlDataReader theReader = theCommand.ExecuteReader())\n            {\n                while (theReader.Read())\n                {\n                    DropDownList1.Items.Add(theReader["ApplicationName"].ToString());\n                }\n            }\n\n        }\n        catch (Exception ex)\n        {\n            // Log the exception details securely.\n            // Show a generic error message to the user.\n            System.Diagnostics.Debug.WriteLine("Error occurred: " + ex.Message);\n            lblErrorMessage.Text = "An error occurred. Please try again later.";\n        }\n    }\n}