using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument doc = new XmlDocument();\n        doc.InnerXml = @"<Element1 attr1=""value1"" xmlns=""http://mynamespace.com/"" attr2=""value2""/>";\n\n        // Create a new node without namespace.\n        XmlElement elementWithoutNamespace = doc.CreateElement("Element1");\n\n        // Copy attributes that are not the xmlns attribute.\n        foreach (XmlAttribute attr in doc.DocumentElement.Attributes)\n        {\n            if (attr.Name != "xmlns")\n            {\n                elementWithoutNamespace.SetAttribute(attr.Name, attr.Value);\n            }\n        }\n\n        // Replace the original element with the new one\n        XmlNode parent = doc.DocumentElement.ParentNode;\n        parent.ReplaceChild(elementWithoutNamespace, doc.DocumentElement);\n\n        Console.WriteLine(doc.OuterXml);  // Outputs: <Element1 attr1="value1" attr2="value2"></Element1>\n    }\n}