using System;\n\npublic class CirclePointCalculator {\n    public static (double, double) PointOnCircle(double radius, double angleDegrees, (double x, double y) origin = (0, 0)) {\n        double angleRadians = angleDegrees * Math.PI / 180;\n        double x = origin.x + radius * Math.Cos(angleRadians);\n        double y = origin.y + radius * Math.Sin(angleRadians);\n        return (x, y);\n    }\n}