public void SaveCalibrationValues(int xLocation, int yLocation, int offsetX, int offsetY)\n{\n    // Define the registry key path\n    string touchKeyPath = @"HARDWARE\TOUCH";\n\n    try\n    {\n        // Open or create the registry key. The second parameter indicates that we have permission to write.\n        using (RegistryKey touchKey = Registry.LocalMachine.CreateSubKey(touchKeyPath, RegistryKeyPermissionCheck.ReadWriteSubTree))\n        {\n            if (touchKey != null)\n            {\n                // Set the calibration values\n                touchKey.SetValue("XLocation", xLocation, RegistryValueKind.DWord);\n                touchKey.SetValue("YLocation", yLocation, RegistryValueKind.DWord);\n                touchKey.SetValue("OffsetX", offsetX, RegistryValueKind.DWord);\n                touchKey.SetValue("OffsetY", offsetY, RegistryValueKind.DWord);\n\n                // Optionally, you can flush the registry key to ensure all changes are written immediately\n                touchKey.Flush();\n            }\n        }\n\n        Console.WriteLine("Calibration values saved successfully.");\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Failed to save calibration values: {ex.Message}");\n    }\n}