using System.Configuration;\n\npublic void UpdateAppSettings()\n{\n    // Open the Web.config file\n    Configuration config = ConfigurationManager.OpenWebConfiguration("~");\n\n    // Get the appSettings section\n    AppSettingsSection appSettings = (AppSettingsSection)config.GetSection("appSettings");\n\n    if (appSettings != null)\n    {\n        // Update or add a setting\n        appSettings.Settings["YourKey"].Value = "NewValue";\n        \n        // If you need to add a new key, check if it exists first\n        if (!appSettings.Settings.AllKeys.Contains("AnotherKey"))\n        {\n            appSettings.Settings.Add("AnotherKey", "AnotherValue");\n        }\n\n        // Save the changes\n        config.Save(ConfigurationSaveMode.Modified);\n    }\n}