[TestMethod]\npublic void SecureSocketLayerSetToTrue()\n{\n    var ldapConnection = new LdapConnection(\n                            new LdapDirectoryIdentifier("ldap.test.com", 636));\n    \n    // Enable SSL/TLS option\n    ldapConnection.SessionOptions.SecureSocketLayer = true;\n    \n    try\n    {\n        // Try to connect to the LDAP server with SSL\n        ldapConnection.Connect();\n        \n        // Verify if the connection is secure\n        Assert.IsTrue(ldapConnection.SessionOptions.SecureSocketLayer);\n    }\n    finally\n    {\n        // Ensure the connection is disposed of properly\n        ldapConnection.Dispose();\n    }\n}