// Storing data in the session securely\nprotected void StoreDataInSession(string key, string value)\n{\n    // Encrypt sensitive data before storing it\n    var encryptedValue = Encrypt(value);\n    Session[key] = encryptedValue;\n}\n\nprivate string Encrypt(string plainText)\n{\n    // Use a secure encryption algorithm and manage keys properly\n    using (Aes aesAlg = Aes.Create())\n    {\n        aesAlg.Key = Encoding.UTF8.GetBytes("YourSecretKey1234567890123");\n        aesAlg.IV = Encoding.UTF8.GetBytes("YourSecretIV1234567");\n\n        ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n\n        using (MemoryStream msEncrypt = new MemoryStream())\n        {\n            using (CryptoStream csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n            {\n                using (StreamWriter swEncrypt = new StreamWriter(csEncrypt))\n                {\n                    swEncrypt.Write(plainText);\n                }\n                return Convert.ToBase64String(msEncrypt.ToArray());\n            }\n        }\n    }\n}\n\n// Retrieving and decrypting data from the session\nprotected string RetrieveDataFromSession(string key)\n{\n    var encryptedValue = Session[key]?.ToString();\n    if (encryptedValue == null) return null;\n\n    return Decrypt(encryptedValue);\n}\n\nprivate string Decrypt(string cipherText)\n{\n    using (Aes aesAlg = Aes.Create())\n    {\n        aesAlg.Key = Encoding.UTF8.GetBytes("YourSecretKey1234567890123");\n        aesAlg.IV = Encoding.UTF8.GetBytes("YourSecretIV1234567");\n\n        ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n\n        using (MemoryStream msDecrypt = new MemoryStream(Convert.FromBase64String(cipherText)))\n        {\n            using (CryptoStream csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n            {\n                using (StreamReader srDecrypt = new StreamReader(csDecrypt))\n                {\n                    return srDecrypt.ReadToEnd();\n                }\n            }\n        }\n    }\n}