using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class ViewStateManager\n{\n    public static byte[] GenerateViewStateMac(byte[] viewStateData, string validationKey)\n    {\n        using (var hmac = new HMACSHA256(Encoding.UTF8.GetBytes(validationKey)))\n        {\n            return hmac.ComputeHash(viewStateData);\n        }\n    }\n\n    // Example Usage\n    public static void Main()\n    {\n        string validationKey = "yourValidationKeyHere"; // This should be the actual key used in your application\n        byte[] viewStateData = Encoding.UTF8.GetBytes("YourViewStateDataBase64String"); // ViewState data after being decoded from base64\n\n        try\n        {\n            byte[] mac = GenerateViewStateMac(viewStateData, validationKey);\n            string macBase64 = Convert.ToBase64String(mac);\n            Console.WriteLine("Generated MAC (base64): " + macBase64);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error generating ViewState MAC: " + ex.Message);\n        }\n    }\n}