using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\npublic class EmbedAppForm : Form\n{\n    public EmbedAppForm()\n    {\n        this.Text = "Embedded Application Example";\n        this.Width = 500;\n        this.Height = 400;\n\n        // Create a panel to host the other application\n        Panel panel = new Panel()\n        {\n            Dock = DockStyle.Fill,\n            BorderStyle = BorderStyle.FixedSingle\n        };\n        this.Controls.Add(panel);\n\n        // Start an external process (e.g., calc.exe)\n        Process calcProcess = new Process();\n        calcProcess.StartInfo.FileName = "calc.exe";\n        calcProcess.EnableRaisingEvents = true;\n        calcProcess.Exited += (sender, args) => CloseApp(calcProcess);\n        calcProcess.Start();\n\n        // Set the calculator's window to be a child of our panel\n        SetParent(calcProcess.MainWindowHandle, panel.Handle);\n\n        // Move and resize the calculator window to fit within our panel\n        MoveWindow(calcProcess.MainWindowHandle, 0, 0, \n                   panel.Width, panel.Height, true);\n    }\n\n    private void CloseApp(Process process)\n    {\n        if (process != null && !process.HasExited) process.CloseMainWindow();\n    }\n\n    [DllImport("user32.dll")]\n    private static extern IntPtr SetParent(IntPtr hWndChild, IntPtr hWndNewParent);\n\n    [DllImport("user32.dll", SetLastError = true)]\n    private static extern bool MoveWindow(IntPtr hWnd, int X, int Y, int nWidth, int nHeight, bool bRepaint);\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new EmbedAppForm());\n    }\n}