using System;\nusing System.Globalization;\n\nclass Program\n{\n    static void Main()\n    {\n        // This is the format with 'zzzzz' representing local time offset\n        string format = "ddd MMM dd HH:mm:ss K yyyy"; // Use 'K' for UTC designator\n\n        // create a UTC time\n        string expected = "Fri Dec 19 17:24:18 +0000 2008";\n        var time = new DateTime(2008, 12, 19, 17, 24, 18, 0, DateTimeKind.Utc);\n\n        // Use 'K' to designate UTC or local time\n        string actual = time.ToString(format, CultureInfo.InvariantCulture);\n\n        Console.WriteLine(actual); // Outputs: Fri Dec 19 17:24:18 +0000 2008\n\n        // Alternatively, use the round-trip format\n        string roundTripFormat = "o";\n        string roundTripActual = time.ToString(roundTripFormat, CultureInfo.InvariantCulture);\n        Console.WriteLine(roundTripActual); // Outputs: 2008-12-19T17:24:18.0000000Z\n    }\n}