using FluentNHibernate.Mapping;\n\npublic class YourEntityMap : ClassMap<YourEntity>\n{\n    public YourEntityMap()\n    {\n        Id(x => x.Id);\n        Map(x => x.ShortDescription).Length(255); // For short strings\n        Map(x => x.LongText).CustomType("StringClob").Length(int.MaxValue); // For long text\n    }\n}\n\npublic class YourEntity\n{\n    public virtual int Id { get; set; }\n    public virtual string ShortDescription { get; set; } // Short, queryable field\n    public virtual string LongText { get; set; } // Large, non-queryable or rarely queried text\n}