using DevExpress.XtraReports.UI;\nusing DevExpress.DataAccess.OData;\n\n// Create a new instance of the XtraReport class.\nXtraReport report = new XtraReport();\n\n// Create an ODataSource component and set its properties to connect to your RESTful service.\nODataSource ods = new ODataService();\nods.DataSourceUri = "https://your-rest-api-url.com/odata";\nods.BoundID = "YourRestService";\n\n// Add the data source to the report's DataBindings.\nreport.DataSource = ods;\n\n// Create and configure a report band (e.g., DetailBand) to display the data.\nDetailBand detailBand = new DetailBand();\nreport.Bands.AddRange(new[] { detailBand });