// In Startup.cs ConfigureServices method or a similar setup with DI\n\npublic void ConfigureServices(IServiceCollection services)\n{\n    string dbProvider = Configuration["DatabaseProvider"];\n\n    if (dbProvider == "SqlServer")\n    {\n        services.AddDbContext<ApplicationDbContext>(options =>\n            options.UseSqlServer(Configuration.GetConnectionString("DefaultConnection")));\n    }\n    else if (dbProvider == "Oracle")\n    {\n        services.AddDbContext<ApplicationDbContext>(options =>\n            options.UseOracle(Configuration.GetConnectionString("OracleConnection")));\n    }\n\n    // Other service configurations...\n}\n\n// appsettings.json example\n{\n  "DatabaseProvider": "SqlServer",\n  "ConnectionStrings": {\n    "DefaultConnection": "Server=(localdb)\\mssqllocaldb;Database=YourDbName;Trusted_Connection=True;",\n    "OracleConnection": "Data Source=(DESCRIPTION=(ADDRESS=(PROTOCOL=tcp)(HOST=localhost)(PORT=1521))(CONNECT_DATA=(SERVICE_NAME=XE)));User Id=YourUserId;Password=YourPass;"\n  }\n}