using System;\nusing System.IO;\nusing System.Security.Cryptography.Pkcs;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        string certificatePath = "path_to_your_certificate.pfx";\n        string certificatePassword = "certificate_password";\n        string inputFile = "path_to_your_application.exe";\n        string outputFile = "signed_application.exe";\n\n        X509Certificate2 certificate = new X509Certificate2(certificatePath, certificatePassword);\n\n        using (FileStream input = File.OpenRead(inputFile))\n        {\n            byte[] content = new byte[input.Length];\n            int bytesRead = input.Read(content, 0, content.Length);\n\n            SignedCms signedCms = new SignedCms(new ContentInfo(content), true);\n            CmsSigner signer = new CmsSigner(certificate);\n            \n            signedCms.ComputeSignature(signer);\n\n            byte[] signedBytes = signedCms.Encode();\n\n            using (FileStream output = File.Create(outputFile))\n            {\n                output.Write(signedBytes, 0, signedBytes.Length);\n            }\n        }\n\n        Console.WriteLine("The application has been successfully signed.");\n    }\n}