// Example of using FluentValidation in ASP.NET\nusing FluentValidation;\n\npublic class CustomerValidator : AbstractValidator<Customer>\n{\n    public CustomerValidator()\n    {\n        RuleFor(customer => customer.Name).NotEmpty();\n        RuleFor(customer => customer.Discount).InclusiveBetween(0, 100);\n        RuleFor(customer => customer.Email).EmailAddress().When(customer => !string.IsNullOrEmpty(customer.Email));\n    }\n}