// Example: Storing files to Azure Blob Storage\npublic async Task UploadToBlobStorage(IFormFile file, string formId)\n{\n    var blobServiceClient = new BlobServiceClient(Environment.GetEnvironmentVariable("AzureWebJobsStorage"));\n    var containerClient = blobServiceClient.GetBlobContainerClient("forms-container");\n    await containerClient.CreateIfNotExistsAsync();\n    var blobClient = containerClient.GetBlobClient(formId + ".docx");\n    await using (var fileStream = file.OpenReadStream())\n    {\n        await blobClient.UploadAsync(fileStream, true);\n    }\n}