// Simple SendGrid implementation for sending emails\npublic class EmailService\n{\n    private readonly IConfiguration _configuration;\n\n    public EmailService(IConfiguration configuration)\n    {\n        _configuration = configuration;\n    }\n\n    public void SendEmail(string email, string messageBody)\n    {\n        var apiKey = Environment.GetEnvironmentVariable("SENDGRID_API_KEY");\n        var client = new SendGridClient(apiKey);\n        var from = new EmailAddress(_configuration["MailSettings:From"], "No-Reply Form System");\n        var subject = "Form requires your approval";\n        var to = new EmailAddress(email, email);\n        var msg = MailHelper.CreateSingleEmail(from, to, subject, messageBody, null);\n        var response = client.SendEmailAsync(msg).Result;\n    }\n}