using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a document object\n        Document document = new Document();\n        \n        try\n        {\n            // Specify the path to write PDF file\n            PdfWriter.GetInstance(document, new FileStream("output.pdf", FileMode.Create));\n            \n            // Open the document for writing\n            document.Open();\n\n            // Add text content to the document\n            Paragraph paragraph = new Paragraph("Hello World! This is a test PDF file generated from .NET 2.0 using iTextSharp.");\n            document.Add(paragraph);\n\n        }\n        catch (DocumentException de)\n        {\n            Console.Error.WriteLine(de.Message);\n        }\n        catch (IOException ioEx)\n        {\n            Console.Error.WriteLine(ioEx.Message);\n        }\n        finally\n        {\n            // Close the document to finish PDF creation.\n            if (document != null) document.Close();\n        }\n\n        Console.WriteLine("PDF created successfully.");\n    }\n}