using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        // List of URLs to export\n        string[] urlsToExport =\n        {\n            "http://yourwebsite.com/page1.aspx",\n            "http://yourwebsite.com/page2.aspx"\n        };\n\n        foreach (var url in urlsToExport)\n        {\n            var html = await FetchHtml(url);\n            SaveToFile(url, html);\n        }\n    }\n\n    static async Task<string> FetchHtml(string url)\n    {\n        try\n        {\n            HttpResponseMessage response = await client.GetAsync(url);\n            response.EnsureSuccessStatusCode();\n            return await response.Content.ReadAsStringAsync();\n        }\n        catch (HttpRequestException e)\n        {\n            Console.WriteLine($"Request error: {e.Message}");\n            return null;\n        }\n    }\n\n    static void SaveToFile(string url, string html)\n    {\n        if (html == null) return;\n\n        // Extract the filename from URL and save HTML content\n        var filename = new Uri(url).Segments.Last().Replace("/", "");\n        File.WriteAllText(filename, html);\n        Console.WriteLine($"Saved: {filename}");\n    }\n}