protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack)\n    {\n        // Assuming the form is submitted and we are handling postback\n        HandlePostedFiles();\n    }\n}\n\nprivate void HandlePostedFiles()\n{\n    for (int i = 0; i < Request.Files.Count; i++)\n    {\n        var file = Request.Files[i];\n\n        if (file != null && file.ContentLength > 0)\n        {\n            // Get a file name from the uploaded file to create\n            // a local version of it on the server.\n            string fileName = Path.GetFileName(file.FileName);\n\n            // Save the uploaded file to the local server directory\n            // in the example below. This also demonstrates using\n            // Server.MapPath to get the physical path on the server\n\n            string pathToSave = Server.MapPath("~/UploadedFiles/" + fileName);\n            try\n            {\n                file.SaveAs(pathToSave);\n\n                // Notify the user of the name of the saved file.\n                Response.Write("The uploaded file has been saved as " + fileName);\n            }\n            catch (Exception ex)\n            {\n                // Notify the user that an error occurred and display the exception text.\n                Response.Write("Error: " + ex.Message);\n            }\n        }\n    }\n}