using System;\nusing System.Windows.Forms;\nusing ScintillaNET;\n\nnamespace HtmlEditorExample\n{\n    public partial class MainForm : Form\n    {\n        private Scintilla scintilla1;\n\n        public MainForm()\n        {\n            InitializeComponent();\n            \n            // Setup the Scintilla control\n            scintilla1 = new Scintilla();\n            scintilla1.Dock = DockStyle.Fill;\n            scintilla1.Lexer = Lexer.Html;\n            scintilla1.StyleResetDefault();\n            scintilla1.Styles[Style.Default].Font = "Verdana";\n            scintilla1.Styles[Style.Default].Size = 10;\n\n            this.Controls.Add(scintilla1);\n        }\n\n        // Example method to show HTML content\n        private void SetHtmlContent(string html)\n        {\n            scintilla1.Text = html;\n        }\n    }\n}