// Assuming you have a DataSet named MyDataSet and TableAdapter named MyTableTableAdapter\n\npublic class DataManager\n{\n    private MyDataSet.MyTableDataTable myTableData;\n\n    public DataManager()\n    {\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        var tableAdapter = new MyTableTableAdapter();\n        myTableData = new MyDataSet.MyTableDataTable();\n        tableAdapter.Fill(myTableData);\n    }\n\n    public MyDataSet.MyTableDataTable GetMyTableData()\n    {\n        return myTableData;\n    }\n\n    // Add a method to refresh data if needed\n    public void RefreshData()\n    {\n        LoadData();\n    }\n}