private void SimulateButtonClickAnimation()\n{\n    // Capture the original background color\n    Color originalColor = button1.BackColor;\n\n    // Change the background color to simulate the button being pressed\n    button1.BackColor = SystemColors.ControlDark; // You can change this to any color\n\n    // Apply a small delay for the animation effect (e.g., 50 milliseconds)\n    System.Threading.Thread.Sleep(50);\n\n    // Restore the original background color\n    button1.BackColor = originalColor;\n}\n\nprivate void imageControl_Click(object sender, EventArgs e)\n{\n    SimulateButtonClickAnimation();\n}