using (FileStream fs = new FileStream(Server.MapPath(GlobalVar.compathver), FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n{\n    DataTable CommentTable = new DataTable("Comment");\n    CommentTable.Columns.Add("ID", typeof(int));\n    CommentTable.Columns.Add("Author", typeof(string));\n    CommentTable.Columns.Add("Date", typeof(string));\n    CommentTable.Columns.Add("Text", typeof(string));\n\n    // Create a DataSet to read the XML schema and data\n    using (DataSet dsTemp = new DataSet())\n    {\n        dsTemp.ReadXml(fs);\n        if (dsTemp.Tables.Count > 0 && dsTemp.Tables[0].Rows.Count > 0)\n        {\n            CommentTable = dsTemp.Tables[0].Clone(); // Clone the structure of the first table in XML\n            foreach (DataRow row in dsTemp.Tables[0].Rows)\n            {\n                CommentTable.ImportRow(row);\n            }\n        }\n    }\n\n    dset.Tables.Add(CommentTable);\n}