using Microsoft.AspNetCore.Mvc;\nusing System.Data.SqlClient;\n\npublic class SmsController : Controller\n{\n    private readonly string _connectionString = "YourSQLConnectionString";\n\n    [HttpPost("sms/receive")]\n    public IActionResult ReceiveSms(string From, string Body)\n    {\n        using (SqlConnection conn = new SqlConnection(_connectionString))\n        {\n            conn.Open();\n            \n            // Assume we have a table 'Deliveries' with columns 'PhoneNumber', 'Message'\n            string sqlQuery = "INSERT INTO Deliveries (PhoneNumber, Message) VALUES (@From, @Body)";\n            SqlCommand cmd = new SqlCommand(sqlQuery, conn);\n            cmd.Parameters.AddWithValue("@From", From);\n            cmd.Parameters.AddWithValue("@Body", Body);\n            \n            cmd.ExecuteNonQuery();\n        }\n\n        // Return a 204 No Content response to satisfy the Twilio webhook requirements\n        return NoContent();\n    }\n}