using System;\nusing System.Text;\n\npublic class InsecureXOREncryption {\n    public static string EncryptOrDecrypt(string input, string key) {\n        StringBuilder result = new StringBuilder();\n        byte[] dataBytes = Encoding.UTF8.GetBytes(input);\n        byte[] keyBytes = Encoding.UTF8.GetBytes(key);\n\n        for (int i = 0; i < dataBytes.Length; i++) {\n            // XOR each byte of the data with the corresponding byte in the key\n            byte xorByte = (byte)(dataBytes[i] ^ keyBytes[i % keyBytes.Length]);\n            result.Append((char)xorByte);\n        }\n\n        return result.ToString();\n    }\n}