protected void lvPhotos_ItemDataBound(object sender, ListViewItemEventArgs e)\n{\n    if (e.Item.ItemType == ListViewItemType.DataItem)\n    {\n        // Find the placeholder to add the Image Control\n        PlaceHolder imgPlaceholder = (PlaceHolder)e.Item.FindControl("imgPlaceholder");\n        \n        // Create a new Image control and set its URL\n        Image photo = new Image();\n        string savedURL = DataBinder.Eval(e.Item.DataItem, "Url").ToString(); // Adjust property name as necessary\n        photo.ImageUrl = "~/photos/" + savedURL;\n        imgPlaceholder.Controls.Add(photo);\n    }\n}