private System.Windows.Forms.Timer animationTimer;\n\npublic YourFormConstructor()\n{\n    // Initialize timer\n    animationTimer = new System.Windows.Forms.Timer();\n    animationTimer.Interval = 50; // in milliseconds\n    animationTimer.Tick += AnimationTimer_Tick;\n}\n\nprivate void SimulateButtonClickAnimation()\n{\n    Color originalColor = button1.BackColor;\n    button1.BackColor = SystemColors.ControlDark;\n\n    // Start the timer to restore the color after the interval\n    animationTimer.Tag = originalColor; // Store the original color for use in the tick event\n    animationTimer.Start();\n}\n\nprivate void AnimationTimer_Tick(object sender, EventArgs e)\n{\n    // Restore the original background color\n    button1.BackColor = (Color)animationTimer.Tag;\n    animationTimer.Stop(); // Stop the timer after one tick\n}\n\nprivate void imageControl_Click(object sender, EventArgs e)\n{\n    SimulateButtonClickAnimation();\n}