public static IList<T> LoadObjectListAll<T>()\n{\n    ISession session = CheckForExistingSession();\n    var cfg = new NHibernate.Cfg.Configuration().Configure();\n    var returnList = session.CreateCriteria(typeof(T));\n    var list = returnList.List<object>(); // Ensure the list is of a known type, such as object\n    var castList = list.Cast<T>().ToList(); // Cast to T and convert to List<T>\n    return castList;\n}