string imageFilePath = Server.MapPath(imageFilename);\n\n// Save chart as PNG ensuring proper disposal and overwriting if needed.\nusing (FileStream fs = new FileStream(imageFilePath, FileMode.Create, FileAccess.Write))\n{\n    chart.SaveImage(fs, ChartImageFormat.Png);\n}\n\nstring pdfFilePath = Server.MapPath("charts.pdf");\n\ntry\n{\n    // Create a PDF document ensuring proper disposal.\n    using (Document doc = new Document())\n    {\n        using (FileStream fs = new FileStream(pdfFilePath, FileMode.Create, FileAccess.Write))\n        {\n            PdfWriter.GetInstance(doc, fs);\n            doc.Open();\n            \n            // Add data to the PDF here\n            \n            doc.Close();\n        }\n    }\n}\ncatch (Exception ex)\n{\n    // Log the exception or handle it appropriately\n    Console.WriteLine("Error creating PDF: " + ex.Message);\n}