// Example pseudo-code for Chilkat library (assuming it's installed)\n   Chilkat.Imap imap = new Chilkat.Imap();\n   bool success;\n\n   imap.Ssl = true;\n   imap.Port = 993;\n   success = imap.Connect("imap.example.com");\n   if (!success) return;\n\n   success = imap.Login("username", "password");\n   if (!success) return;\n\n   Chilkat.MessageSet messageSet = imap.Search("ALL", false);\n   int numEmails = (messageSet == null) ? 0 : messageSet.Count;\n   for (int i = 0; i < numEmails; i++)\n   {\n       // Fetch the emails\n       Chilkat.Email email = imap.FetchByUid(messageSet.GetId(i), true);\n   }\n\n   imap.Disconnect();