private void SendMailToAdminToApprove(string email_, string name_)\n{\n    MailMessage msg = new MailMessage();\n    msg.From = new MailAddress("your-email@gmail.com", "Your Name");\n    msg.To.Add(new MailAddress(email_, name_));\n    msg.Subject = "Message Subject";\n    msg.Body = "Mail body content";\n    msg.IsBodyHtml = true;\n    msg.Priority = MailPriority.High;\n\n    try\n    {\n        SmtpClient c = new SmtpClient("smtp.gmail.com", 587);\n        c.Credentials = new System.Net.NetworkCredential("your-email@gmail.com", "your-password");\n        c.EnableSsl = true;\n        c.Send(msg);\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Error sending email: {ex.Message}");\n    }\n}