private bool isShiftPressed = false;\n\nprivate void Form1_KeyDown(object sender, KeyEventArgs e)\n{\n    // Check if the Shift key is pressed down.\n    if (e.KeyCode == Keys.ShiftKey)\n        isShiftPressed = true;\n}\n\nprivate void Form1_KeyUp(object sender, KeyEventArgs e)\n{\n    // Check if the key released is '8' and Shift was pressed to determine '*'\n    if (e.KeyCode == Keys.D8 && isShiftPressed)\n    {\n        // The asterisk (*) key was "released"\n        Console.WriteLine("Asterisk key detected on KeyUp event!");\n    }\n\n    // Reset Shift key status\n    if (e.KeyCode == Keys.ShiftKey)\n        isShiftPressed = false;\n}