// Example of using Aspose.Imaging for AI files\n// Install-Package Aspose.Imaging\n\nusing Aspose.Imaging;\nusing Aspose.Imaging.FileFormats.Emf.Graphics;\n\npublic Image ConvertAiToThumbnail(string aiFilePath)\n{\n    using (Image image = Image.Load(aiFilePath))\n    {\n        // Resize the frame with proportional scaling\n        image.Resize(200, 200);\n\n        return new Bitmap(image);\n    }\n}