using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseAttacher\n{\n    public static void AttachMdfFile(string serverInstance, string databaseName, string mdfFilePath, string ldfFilePath)\n    {\n        using (SqlConnection conn = new SqlConnection($"Server={serverInstance};Integrated Security=True;"))\n        {\n            conn.Open();\n\n            string attachQuery =\n                $"CREATE DATABASE [{databaseName}] ON (FILENAME = N'{mdfFilePath}'), " +\n                $"(FILENAME = N'{ldfFilePath}') FOR ATTACH;";\n\n            using (SqlCommand cmd = new SqlCommand(attachQuery, conn))\n            {\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string serverInstance = @".\SQLEXPRESS"; // Default instance name for SQL Server Express\n        string databaseName = "YourDatabaseName";\n        string mdfFilePath = @"C:\Path\To\YourDatabaseFile.mdf";\n        string ldfFilePath = @"C:\Path\To\YourLogFile.ldf";\n\n        try\n        {\n            AttachMdfFile(serverInstance, databaseName, mdfFilePath, ldfFilePath);\n            Console.WriteLine("Database attached successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}