using System;\nusing LinqToTwitter;\n\nclass Program\n{\n    static void Main()\n    {\n        var auth = new ApplicationOnlyAuthorizer\n        {\n            CredentialStore = new InMemoryCredentialStore\n            {\n                ConsumerKey = "your-consumer-key",\n                ConsumerSecret = "your-consumer-secret"\n            }\n        };\n\n        auth.AuthorizeAsync().Wait();\n\n        using (var ctx = new TwitterContext(auth))\n        {\n            // Attempt a simple API call to verify credentials\n            var verifyResponse = (await \n                (from account in ctx.VerifyCredentials where account.Type == VerifyCredentialsType.BearerToken select account)\n                .ToListAsync());\n\n            if (verifyResponse.Count > 0 && verifyResponse[0].ScreenName != null)\n            {\n                Console.WriteLine("Authentication successful.");\n            }\n            else\n            {\n                Console.WriteLine("Authentication failed.");\n            }\n        }\n    }\n}