using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set the console title\n        Console.Title = "Advanced Console IO";\n\n        // Write some text at location (10, 5) with foreground color Red and background Blue\n        DrawText(10, 5, ConsoleColor.Red, ConsoleColor.Blue, "Hello World!");\n\n        // Read key to prevent window from closing immediately\n        Console.ReadKey();\n    }\n\n    static void DrawText(int x, int y, ConsoleColor foregroundColor, ConsoleColor backgroundColor, string text)\n    {\n        // Set the cursor position\n        Console.SetCursorPosition(x, y);\n\n        // Set the colors\n        Console.ForegroundColor = foregroundColor;\n        Console.BackgroundColor = backgroundColor;\n\n        // Write the text\n        Console.Write(text);\n\n        // Reset colors to default\n        Console.ResetColor();\n    }\n}