using System;\nusing System.Net;\nusing System.Net.Mail;\n\npublic class EmailSender {\n    public void SendEmail() {\n        // Configure ServicePointManager to use TLS 1.2 or higher\n        ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Tls13;\n\n        // For debugging, temporarily ignore server certificate errors.\n        // This should not be used in production as it makes the connection insecure.\n        //ServicePointManager.ServerCertificateValidationCallback += (sender, cert, chain, sslPolicyErrors) => true;\n        \n        using (var client = new SmtpClient("smtp.gmail.com", 587)) {\n            client.EnableSsl = true;\n            client.Credentials = new NetworkCredential("your-email@gmail.com", "your-password");\n            \n            try {\n                var mailMessage = new MailMessage("from-email@gmail.com", "to-email@example.com", "Subject", "Body");\n                client.Send(mailMessage);\n                Console.WriteLine("Email sent successfully!");\n            } catch (Exception ex) {\n                Console.WriteLine($"Failed to send email: {ex.Message}");\n            }\n        }\n    }\n}