// Login method\nprotected void LogIn()\n{\n    string userId = GetUserIdFromDatabase(txtUsername.Text, txtPassword.Text);\n    if (userId != null)\n    {\n        // Store user ID in session state\n        Session["UserID"] = userId;\n        Response.Redirect("~/Home.aspx");\n    }\n}\n\n// On Page_Load or in a base page class\nprotected void CheckUserSession()\n{\n    if (Session["UserID"] == null)\n    {\n        // Redirect to login because the user is not logged in\n        Response.Redirect("~/LogIn.aspx");\n    }\n}