using System;\nusing System.Net;\nusing System.Net.Sockets;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var client = new TcpClient();\n        // Bind to a specific IP address before connecting\n        ((System.Net.Sockets.Socket)client.Client).Bind(new IPEndPoint(IPAddress.Parse("10.10.10.1"), 0));\n\n        client.Connect("10.10.10.2", 1433); // Connect using the bound IP\n\n        Console.WriteLine("Connected to SQL Server");\n    }\n}