private void dataGridView1_CellPainting(object sender, DataGridViewCellPaintingEventArgs e)\n{\n    if (e.RowIndex >= 0 && e.ColumnIndex >= 0)\n    {\n        // Check if the current cell is the active cell\n        if (dataGridView1.CurrentCellAddress.X == e.ColumnIndex && \n            dataGridView1.CurrentCellAddress.Y == e.RowIndex)\n        {\n            // Change the background color of the current cell\n            using (Brush b = new SolidBrush(Color.LightBlue))  // Use any preferred color\n            {\n                e.Graphics.FillRectangle(b, e.CellBounds);\n            }\n\n            // Paint the content of the cell\n            e.Paint(e.CellBounds, DataGridViewPaintParts.All & ~DataGridViewPaintParts.Background);\n\n            // Indicate that we've handled painting\n            e.Handled = true;\n        }\n    }\n}