using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "https://www.example.com";\n        \n        try\n        {\n            Process.Start(new ProcessStartInfo(url) { UseShellExecute = true });\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Unable to open URL: " + ex.Message);\n        }\n    }\n}