using System;\nusing System.IO;\nusing SharpSvn;\n\nclass Program\n{\n    private static SvnClient client = new SvnClient();\n\n    static void Main(string[] args)\n    {\n        string repoUrl = "http://your-svn-repository-url";\n        string filePath = @"C:\path\to\your\report.xlsx";\n        string localCopyPath = Path.Combine(Path.GetTempPath(), "DailyReport.xlsx");\n\n        // Check out the repository to a temporary location\n        client.CheckOut(repoUrl, Path.GetTempPath());\n\n        if (File.Exists(localCopyPath))\n        {\n            File.Copy(filePath, localCopyPath, true);\n        }\n        else\n        {\n            File.Move(filePath, localCopyPath);\n        }\n\n        // Add file to SVN\n        if (!client.Status(localCopyPath).IsVersioned)\n        {\n            client.Add(new SvnAddEventArgs { Path = localCopyPath, Depth = SvnDepth.Empty });\n        }\n\n        // Commit changes\n        using (SvnCommitArgs commitArgs = new SvnCommitArgs())\n        {\n            commitArgs.LogMessage = "Daily Report Update";\n            Collection<SvnStatusEventArgs> status;\n            client.GetStatus(localCopyPath, out status);\n            \n            foreach (var st in status)\n            {\n                if (!st.IsVersioned && !st.LocalContentModified) continue;\n\n                SvnTarget target = new SvnTarget(st.FullPath);\n                commitArgs.Targets.Add(target);\n            }\n\n            CommitResult result;\n            client.Commit(commitArgs, out result);\n\n            Console.WriteLine($"Committed revision: {result.Revision}");\n        }\n    }\n}