public string GetDatabaseConnectionString(int userId)\n{\n    // Simple round-robin logic based on user ID modulo 3\n    int serverIndex = userId % 3;\n    switch (serverIndex)\n    {\n        case 0:\n            return "Data Source=PC1;Initial Catalog=YourDB;Integrated Security=True;";\n        case 1:\n            return "Data Source=PC2;Initial Catalog=YourDB;Integrated Security=True;";\n        case 2:\n            return "Data Source=PC3;Initial Catalog=YourDB;Integrated Security=True;";\n        default:\n            throw new InvalidOperationException("Invalid server index");\n    }\n}