protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        int numberOfTextBoxes = 30; // Example number\n        CreateDynamicTextBoxes(numberOfTextBoxes);\n    }\n}\n\nprivate void CreateDynamicTextBoxes(int count)\n{\n    for (int i = 1; i <= count; i++)\n    {\n        TextBox textBox = new TextBox\n        {\n            ID = "txt" + i,\n            CssClass = "dynamic-textbox"\n        };\n        form1.Controls.Add(textBox);\n    }\n}