using System;\nusing System.DirectoryServices.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var context = new DirectoryContext())\n        {\n            // Query to get distinct departments from users in a specific OU\n            var departments = (from user in context.Queryable<User>()\n                               where user.DistinguishedName.StartsWith("OU=SomeOU,DC=example,DC=com")\n                               select user.Department).Distinct();\n\n            foreach (var department in departments)\n            {\n                Console.WriteLine(department);\n            }\n        }\n    }\n}