// Example of creating a window in GTK#\nusing Gtk;\n\npublic class Program {\n    private static void Main(string[] args) {\n        Application.Init();\n        Window win = new Window("GTK# Application");\n        win.DefaultWidth = 200;\n        win.DefaultHeight = 200;\n        win.DeleteEvent += delegate { Application.Quit(); };\n        win.ShowAll();\n        Application.Run();\n    }\n}